package br.com.viasoft.LojadeLivros.controller;

import br.com.viasoft.LojadeLivros.model.Produto;
import br.com.viasoft.LojadeLivros.repository.ProdutoRepository;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;

import javax.persistence.EntityManager;
import java.util.ArrayList;
import java.util.List;

@Controller
public class HomeController {

    @Autowired
    private ProdutoRepository produtoRepository;

    @GetMapping("/")
    public String getHome(Model model){

        List<Produto> listaDeLivros = produtoRepository.findAll();
        model.addAttribute("lista", listaDeLivros);

        return "home";

    }
}
